const express = require("express");
const path = require("path");
const port = 7000;
const app = express();

app.use("single", express.static(path.resolve("index.js")));

app.get("/*", (req,res) => {
    res.sendFile(path.resolve("single", "index.html"));
});

app.listen(port, () =>{
    console.log("http://localhost:7000")
})